(152, 1, '2022-06-19 02:05:58', '2022-06-19 02:05:58', '<!-- wp:paragraph {\"align\":\"center\"} -->\n<p class=\"has-text-align-center\"><a href=\"https://lifterlms.com/docs/getting-started-with-the-course-template/\" target=\"_blank\" rel=\"noreferrer noopener\">👉 How To Use This Template &nbsp;👈</a></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:columns {\"verticalAlignment\":\"center\"} -->\n<div class=\"wp-block-columns are-vertically-aligned-center\"><!-- wp:column {\"verticalAlignment\":\"center\"} -->\n<div class=\"wp-block-column is-vertically-aligned-center\"><!-- wp:heading -->\n<h2>Attention Grabbing Course Hook</h2>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>Course promise. This is your opportunity to entice your prospective learner to enroll in the course. Most important thing is to keep it simple, primary benefit focused, and to the point.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:buttons -->\n<div class=\"wp-block-buttons\"><!-- wp:button -->\n<div class=\"wp-block-button\"><a class=\"wp-block-button__link\" href=\"#pricing\">Enroll Now!</a></div>\n<!-- /wp:button --></div>\n<!-- /wp:buttons --></div>\n<!-- /wp:column -->\n\n<!-- wp:column {\"verticalAlignment\":\"center\"} -->\n<div class=\"wp-block-column is-vertically-aligned-center\"><!-- wp:embed {\"url\":\"https://gocodebox.wistia.com/medias/ixgrrk3aup\",\"type\":\"video\",\"providerNameSlug\":\"wistia-inc\",\"className\":\"\"} -->\n<figure class=\"wp-block-embed is-type-video is-provider-wistia-inc\"><div class=\"wp-block-embed__wrapper\">\nhttps://gocodebox.wistia.com/medias/ixgrrk3aup\n</div></figure>\n<!-- /wp:embed --></div>\n<!-- /wp:column --></div>\n<!-- /wp:columns -->\n\n<!-- wp:separator {\"opacity\":\"css\"} -->\n<hr class=\"wp-block-separator has-css-opacity\"/>\n<!-- /wp:separator -->\n\n<!-- wp:heading {\"textAlign\":\"center\",\"level\":4} -->\n<h4 class=\"has-text-align-center\">Trusted By These Brands And Influencers To Get The Results They Want!</h4>\n<!-- /wp:heading -->\n\n<!-- wp:columns -->\n<div class=\"wp-block-columns\"><!-- wp:column -->\n<div class=\"wp-block-column\"><!-- wp:image {\"align\":\"center\",\"id\":34201,\"sizeSlug\":\"large\"} -->\n<figure class=\"wp-block-image aligncenter size-large\"><img src=\"https://academy.lifterlms.com/wp-content/uploads/2021/01/793734_5FakePlaceholderLogo_2_081220.png\" alt=\"\" class=\"wp-image-34201\"/></figure>\n<!-- /wp:image --></div>\n<!-- /wp:column -->\n\n<!-- wp:column -->\n<div class=\"wp-block-column\"><!-- wp:image {\"align\":\"center\",\"id\":34202,\"sizeSlug\":\"large\"} -->\n<figure class=\"wp-block-image aligncenter size-large\"><img src=\"https://academy.lifterlms.com/wp-content/uploads/2021/01/793734_5FakePlaceholderLogo_1_081220.png\" alt=\"\" class=\"wp-image-34202\"/></figure>\n<!-- /wp:image --></div>\n<!-- /wp:column -->\n\n<!-- wp:column -->\n<div class=\"wp-block-column\"><!-- wp:image {\"align\":\"center\",\"id\":34204,\"sizeSlug\":\"large\"} -->\n<figure class=\"wp-block-image aligncenter size-large\"><img src=\"https://academy.lifterlms.com/wp-content/uploads/2021/01/793734_5FakePlaceholderLogo_4_081220.png\" alt=\"\" class=\"wp-image-34204\"/></figure>\n<!-- /wp:image --></div>\n<!-- /wp:column -->\n\n<!-- wp:column -->\n<div class=\"wp-block-column\"><!-- wp:image {\"align\":\"center\",\"id\":34203,\"sizeSlug\":\"large\"} -->\n<figure class=\"wp-block-image aligncenter size-large\"><img src=\"https://academy.lifterlms.com/wp-content/uploads/2021/01/793734_5FakePlaceholderLogo_5_081220.png\" alt=\"\" class=\"wp-image-34203\"/></figure>\n<!-- /wp:image --></div>\n<!-- /wp:column -->\n\n<!-- wp:column -->\n<div class=\"wp-block-column\"><!-- wp:image {\"align\":\"center\",\"id\":34205,\"sizeSlug\":\"large\"} -->\n<figure class=\"wp-block-image aligncenter size-large\"><img src=\"https://academy.lifterlms.com/wp-content/uploads/2021/01/793734_5FakePlaceholderLogo_3_081220.png\" alt=\"\" class=\"wp-image-34205\"/></figure>\n<!-- /wp:image --></div>\n<!-- /wp:column --></div>\n<!-- /wp:columns -->\n\n<!-- wp:cover {\"url\":\"https://academy.lifterlms.com/wp-content/uploads/2021/01/802827_CourseSalesPage_1A_0814720.png\",\"id\":34222,\"dimRatio\":50,\"overlayColor\":\"theme-palette3\",\"minHeight\":400,\"isDark\":false,\"align\":\"full\"} -->\n<div class=\"wp-block-cover alignfull is-light\" style=\"min-height:400px\"><span aria-hidden=\"true\" class=\"wp-block-cover__background has-theme-palette-3-background-color has-background-dim\"></span><img class=\"wp-block-cover__image-background wp-image-34222\" src=\"https://academy.lifterlms.com/wp-content/uploads/2021/01/802827_CourseSalesPage_1A_0814720.png\" data-object-fit=\"cover\"/><div class=\"wp-block-cover__inner-container\"><!-- wp:paragraph {\"align\":\"center\",\"placeholder\":\"Write title…\",\"fontSize\":\"large\"} -->\n<p class=\"has-text-align-center has-large-font-size\">Are You Ready To Transform And Defeat The Enemy?</p>\n<!-- /wp:paragraph --></div></div>\n<!-- /wp:cover -->\n\n<!-- wp:heading {\"textAlign\":\"center\"} -->\n<h2 class=\"has-text-align-center\">Our Journey Together...</h2>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph {\"align\":\"center\"} -->\n<p class=\"has-text-align-center\">Is this the way to fix problem/get result? Talk about the pain and the opportunity that this course addresses. Provide some statistics about the problem/opportunity for logical buyers. Talk about who they are now and who they will become if they invest in the course and do the learning and implementation.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:columns -->\n<div class=\"wp-block-columns\"><!-- wp:column -->\n<div class=\"wp-block-column\"><!-- wp:heading {\"textAlign\":\"center\",\"level\":3} -->\n<h3 class=\"has-text-align-center\">Before Your Course</h3>\n<!-- /wp:heading -->\n\n<!-- wp:image {\"align\":\"center\",\"id\":212,\"sizeSlug\":\"large\"} -->\n<figure class=\"wp-block-image aligncenter size-large\"><img src=\"https://academy.lifterlms.com/wp-content/uploads/2020/08/Copy-of-Untitled-1.jpg\" alt=\"\" class=\"wp-image-212\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:paragraph {\"align\":\"center\"} -->\n<p class=\"has-text-align-center\">Talk about the emotions, environment, and facts of their current situation.</p>\n<!-- /wp:paragraph --></div>\n<!-- /wp:column -->\n\n<!-- wp:column -->\n<div class=\"wp-block-column\"><!-- wp:heading {\"textAlign\":\"center\",\"level\":3} -->\n<h3 class=\"has-text-align-center\">After Your Course</h3>\n<!-- /wp:heading -->\n\n<!-- wp:image {\"id\":211,\"sizeSlug\":\"large\"} -->\n<figure class=\"wp-block-image size-large\"><img src=\"https://academy.lifterlms.com/wp-content/uploads/2020/08/Copy-of-Untitled.jpg\" alt=\"\" class=\"wp-image-211\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:paragraph {\"align\":\"center\"} -->\n<p class=\"has-text-align-center\">Talk about the emotions, environment, and facts of their future situation.</p>\n<!-- /wp:paragraph --></div>\n<!-- /wp:column --></div>\n<!-- /wp:columns -->\n\n<!-- wp:cover {\"url\":\"https://academy.lifterlms.com/wp-content/uploads/2021/01/802827_CourseSalesPage_6A_0814720.png\",\"id\":34219,\"dimRatio\":50,\"overlayColor\":\"theme-palette3\",\"minHeight\":400,\"isDark\":false,\"align\":\"full\"} -->\n<div class=\"wp-block-cover alignfull is-light\" style=\"min-height:400px\"><span aria-hidden=\"true\" class=\"wp-block-cover__background has-theme-palette-3-background-color has-background-dim\"></span><img class=\"wp-block-cover__image-background wp-image-34219\" src=\"https://academy.lifterlms.com/wp-content/uploads/2021/01/802827_CourseSalesPage_6A_0814720.png\" data-object-fit=\"cover\"/><div class=\"wp-block-cover__inner-container\"><!-- wp:paragraph {\"align\":\"center\",\"placeholder\":\"Write title…\",\"fontSize\":\"large\"} -->\n<p class=\"has-text-align-center has-large-font-size\">Course Feedback</p>\n<!-- /wp:paragraph --></div></div>\n<!-- /wp:cover -->\n\n<!-- wp:heading {\"textAlign\":\"center\",\"level\":3} -->\n<h3 class=\"has-te